mapper.select:
		select *
        from (select rownum as rnum, B.* from board B order by num desc) A 
        where A.rnum between #{pm.startCount} and #{pm.endCount}

mapper.update:
update board 
set name=#{name}, title=#{title}, content=#{content}
where num=#{num}

update board
set count=count+1
where num=#{num}
